




select sc.Codigo as nosocio,
p.Nombre, c.Codigo, c.Descripcion
, op.Fecha, concat_ws('-',datepart(year,op.Fecha),datepart(month,op.Fecha))
, o.Codigo, op.Folio, tf.IdTransaccion
, tf.IdTipoSubOperacion, tf.MontoSubOperacion
from dbo.tGRLoperaciones op  WITH(NOLOCK) 
INNER JOIN dbo.tCTLtiposOperacion o  WITH(NOLOCK) 
	on o.IdTipoOperacion = op.IdTipoOperacion
LEFT JOIN dbo.tSDOtransaccionesFinancieras tf  WITH(NOLOCK) 
	on tf.IdOperacion = op.IdOperacion
		and tf.IdEstatus=1
			and tf.IdTipoSubOperacion=501
LEFT JOIN dbo.tAYCcuentas c  WITH(NOLOCK) 
	on c.IdCuenta = tf.IdCuenta
LEFT JOIN dbo.tSCSsocios sc  WITH(NOLOCK) 
	on sc.IdSocio = op.IdSocio
LEFT JOIN dbo.tGRLpersonas p  WITH(NOLOCK) 
	on p.IdPersona = op.IdPersona
